stylesheet:
* {
	box-sizing: border-box;
}

html{
	height: 100%;
}

body{
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	align-items: center;
	min-height: 100%;
	min-height: 100vh;
	min-height: -webkit-fill-available;
	padding: 0;
	margin: 0;
	touch-action: manipulation;
	font-family: -apple-system, system-ui, BlinkMacSystemFont, "Segoe UI", Arial, sans-serif;
}

.a-headline{
	margin-top: 0;
	margin-bottom: 0.75em;
	display: block;
}

label{
	display: block;
	overflow: hidden;
	white-space: nowrap;
	text-overflow: ellipsis;
}

.a-link,
.a-weblink{
	color: inherit;
	text-decoration: none;
	cursor: pointer;
}

.a-interactive{
	cursor: pointer;
}

.a-button[disabled]{
		pointer-events: none;
	}

.a-text{
	display: block;
	margin: 0;
}

.a-image,
.a-icon{
	display: block;
	width: auto;
	height: auto;
}

.a-vstack,
.a-hstack{
	display: flex;
	justify-content: flex-start;
	align-items: flex-start;
}

.a-vstack{
	flex-direction: column;
}

.a-absolute{
	position: relative;
	width: 100%;
	height: 100%;
}

.a-absolute > *{
	position: absolute !important;
	top: 0;
	left: 0;
}

.a-vscroll{
	overflow-y: auto;
	position: relative;
	width: 100%;
}

.a-vscroll > div{
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
}

.a-page{
	display: flex;
	flex-direction: column;
	width: 100%;
	height: 100%;
}

.a-overlay{
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	z-index: 100;
}

.a-fileinput.custom{
	display: none;
}

.a-fileinput{
	white-space: inherit;
	overflow: visible;
}

.a-fileinput > *{
		pointer-events: none;
	}

.a-canvas-wrap{
	position: relative;
	width: 100%;
	height: 100%;
}

.a-canvas-wrap > canvas{
		position: absolute;
		top: 0;
		left: 0;
	}
.pkcnb .a-root,.pkcnb.a-root{
	line-height: 1.75;
	font-family: -apple-system, system-ui, BlinkMacSystemFont, "Segoe UI", Arial, sans-serif;
	background-color: #111111;
	color: #f4f4f4;
	padding: 0 20px;
}

.pkcnb .header,.pkcnb.header{
	width: 100%;
	max-width: 640px;
	padding: 60px 0 40px 0;
	border-bottom: solid 1px #333;
}

.pkcnb .header .a-headline,.pkcnb.header .a-headline{
		font-size: 48px;
		margin-bottom: 0;
	}

.pkcnb .header .a-text,.pkcnb.header .a-text{
		font-size: 18px;
		color: #777;
	}

.pkcnb .body,.pkcnb.body{
	padding-top: 30px;
	padding-bottom: 100px;
	width: 100%;
	max-width: 640px;
	min-height: calc(100vh - 370px);
}

.pkcnb .body Form,.pkcnb.body Form{
		width: 100%;
	}

.pkcnb .body .question,.pkcnb.body .question{
		border: solid 2px transparent;
		border-radius: 15px;
		background-color: #222;
		color: inherit;
		font-size: 18px;
		width: 100%;
		min-height: 120px;
		padding: 15px;
		box-sizing: border-box;
		margin-bottom: 40px;
	}

.pkcnb .body .question:focus,.pkcnb.body .question:focus{
			border-color: dodgerblue;
		}

.pkcnb .body .question:disabled,.pkcnb.body .question:disabled{
			color: #ccc;
		}

.pkcnb .body .question[invalid],.pkcnb.body .question[invalid]{
			border-color: red;
		}

.pkcnb .body .response,.pkcnb.body .response{
		gap: 20px;
	}

.pkcnb .body .response .robot,.pkcnb.body .response .robot{
			border-radius: 100px;
			width: 70px;
			height: 70px;
			vertical-align: middle;
			line-height: 70px;
			text-align: center;
			font-size: 30px;
			background-color: #222;
			flex-shrink: 0;
		}

.pkcnb .body .response .bubble,.pkcnb.body .response .bubble{
			background-color: #222;
			padding: 20px;
			border-radius: 15px;
		}

.pkcnb .body .response .bubble .a-text,.pkcnb.body .response .bubble .a-text{
				max-width: 510px;
				overflow-wrap: break-word;
			}

.pkcnb .body .response .bubble .error,.pkcnb.body .response .bubble .error{
				font-weight: bold;
				color: red;
			}

.pkcnb .body .response .bubble .dots .a-text,.pkcnb.body .response .bubble .dots .a-text{
					font-size: 35px;
					line-height: 18px;
					animation-name: blink;
					animation-duration: 1.4s;
					animation-iteration-count: infinite;
					animation-fill-mode: both;
				}

.pkcnb .body .response .bubble .dots .a-text:nth-child(2),.pkcnb.body .response .bubble .dots .a-text:nth-child(2){
						animation-delay: .2s;
					}

.pkcnb .body .response .bubble .dots .a-text:nth-child(3),.pkcnb.body .response .bubble .dots .a-text:nth-child(3){
						animation-delay: .4s;
					}

.pkcnb .body .again,.pkcnb.body .again{
		width: 100%;
		margin-top: 70px;
		align-items: center;
	}

.pkcnb .body .again .reset,.pkcnb.body .again .reset{
			width: 100%;
			max-width: 260px;
			padding: 20px 30px;
			font-size: 16px;
			color: white;
			border-radius: 22px;
			background-color: dodgerblue;
			border: none;
			cursor: pointer;
		}

.pkcnb .body .again .reset:active,.pkcnb.body .again .reset:active{
				opacity: 0.6;
			}

.pkcnb .powered,.pkcnb.powered{
	color: #ccc;
	margin-top: 50px;
	margin-bottom: 30px;
}

@keyframes blink {
	0% {
		opacity: .2;
	}
	20% {
		opacity: 1;
	}
	100% {
		opacity: .2;
	}
}
